    {
        [SuppressMessage("Microsoft.Performance", "CA1811:AvoidUncalledPrivateCode",
            Justification = "[GeneratedCode] This method has been overridden locally.")]
        internal static MonadOr<IEnumerable<TSource>> CollectCore<TSource>(
            this IEnumerable<MonadOr<TSource>> @this)
        {
            // No need to check for null-reference, "Enumerable.Aggregate" is an extension method.
            Contract.Requires(@this != null);
            Contract.Ensures(Contract.Result<MonadOr<IEnumerable<TSource>>>() != null);

            var seed = MonadOr.Return(Enumerable.Empty<TSource>());
            Func<MonadOr<IEnumerable<TSource>>, MonadOr<TSource>, MonadOr<IEnumerable<TSource>>> fun
                = (m, n) => m.Bind(list => CollectCore_(n, list));

            return @this.Aggregate(seed, fun).AssumeNotNull_();
        }
        
        // NB: We do not inline this method to avoid the creation of an unused private field (CA1823 warning).
        private static MonadOr<IEnumerable<TSource>> CollectCore_<TSource>(
            MonadOr<TSource> m, 
            IEnumerable<TSource> list)
        {
            Contract.Requires(m != null);

            return m.Bind(item => MonadOr.Return(list.Concat(Enumerable.Repeat(item, 1))));
        }

        [SuppressMessage("Microsoft.Performance", "CA1811:AvoidUncalledPrivateCode",
            Justification = "[GeneratedCode] This method has been overridden locally.")]
        internal static MonadOr<TSource> SumCore<TSource>(
            this IEnumerable<MonadOr<TSource>> @this)
        {
            // No need to check for null-reference, "Enumerable.Aggregate" is an extension method. 
            Contract.Requires(@this != null);
            Contract.Ensures(Contract.Result<MonadOr<TSource>>() != null);

            return @this.Aggregate(MonadOr<TSource>.None, (m, n) => m.OrElse(n)).AssumeNotNull_();
        }

        /// <summary>
        /// Instructs code analysis tools to assume that the specified value is not null,
        /// even if it cannot be statically proven not to be <see langword="null"/>.
        /// Inlining the method should remove any performance concern.
        /// </summary>
        [DebuggerHidden]
        [global::System.Runtime.CompilerServices.MethodImpl(global::System.Runtime.CompilerServices.MethodImplOptions.AggressiveInlining)]
        private static T AssumeNotNull_<T>(this T @this) where T : class
        {
            Contract.Ensures(Contract.Result<T>() == @this);
            Contract.Ensures(Contract.Result<T>() != null);
            Contract.Assume(@this != null);

            return @this;
        }
    } // End of the class EnumerableMonadOrExtensions.

