            Contract.Requires(@this != null);
            Contract.Requires(second != null);
            Contract.Ensures(Contract.Result<MonadOr<IEnumerable<TResult>>>() != null);

            Func<TFirst, TSecond, MonadOr<TResult>> resultSelector
