            this IEnumerable<Either<TSource, TRight>> source)
        {
            Debug.Assert(source != null);

            var unit = Either<Unit, TRight>.OfLeft(Unit.Default);
            var item = default(TSource);

            using (var iter = source.GetEnumerator())
