        }

        private static IEnumerable<TSource> CollectIterator<TSource>(IEnumerable<Result<TSource>> source)
        {
            Demand.NotNull(source);

            var item = default(TSource);

            using (var iter = source.GetEnumerator())
            {
                while (iter.MoveNext())
                {
                    var append = false;

                    iter.Current.Bind(
                        val =>
                        {
                            append = true;
                            item = val;

                            return Result.Unit;
                        });

                    if (append) { yield return item; }
                }
            }
        }
    }
}

